import com.cj.mps3.entity.JobOrder;
import java.util.*;
import xyz.elidom.sys.util.ValueUtil;
import xyz.elidom.exception.server.ElidomValidationException;

def sql = """
	select com_cd, cust_cd from (
		select
			com_cd, cust_cd, count(1) as cnt
		from (
			select
				distinct com_cd, cust_cd, order_group
			from
				tb_if_order
			where
				domain_id = ${domainId}
				and batch_id = '${batch.id}')
		group by
			com_cd, cust_cd
	) where 
		cnt > 1
"""

queryDsl.init('com.cj.mps3.entity.JobOrder');
def dupList = queryDsl.selectSql(sql.toString(), 0, 0);

if(ValueUtil.isNotEmpty(dupList)) {
	def dupData = dupList[0];
	def comCd = dupData['com_cd'];
	def custCd = dupData['cust_cd'];
	throw new ElidomValidationException("차량 중복 : 고객사 ${comCd}, 거래처 ${custCd}");
} else {
	return true;
}

import com.cj.mps3.entity.Printer;
import com.cj.mps3.entity.JobOrder;
import com.cj.mps3.entity.BoxResult;
import xyz.elidom.dev.entity.DiyTemplate;
import com.cj.mps3.rest.PrinterController;
import xyz.elidom.dev.rest.DiyServiceController;
import xyz.elidom.dev.rest.DiyTemplateController;
import xyz.elidom.sys.SysConstants;
import xyz.elidom.sys.util.ValueUtil;
import xyz.elidom.util.BeanUtil;

def printBarcodeLabel(long domainId, Printer printer, int printCount, int printPage, BoxResult box, List<JobOrder> orders, int totalEaCount) {
	if(printer != null) {
		String command = generateCommand(printPage, box, orders, totalEaCount);

		if(command != null) {
			printCount = (printCount > 0) ? printCount : 1;
			PrinterController printerCtrl = BeanUtil.get(PrinterController.class);

			for(int i = 0 ; i < printCount ; i++) {
				printerCtrl.printLabelByLabelCommand(printer.getPrinterAgentUrl(), printer.getPrinterDriver(), command);
			}
		}
	}
}

def generateCommand(int printPage, BoxResult box, List<JobOrder> orders, int totalEaCount) {
	DiyTemplateController diyTemplateCtrl = BeanUtil.get(DiyTemplateController.class);
	DiyTemplate template = BeanUtil.get(DiyTemplateController.class).findOne(SysConstants.SHOW_BY_NAME_METHOD, 'DAS_INVOICE_LABEL');

	if(template != null) {
		template = diyTemplateCtrl.dynamicTemplate(template.getId(), ValueUtil.newMap("box,orders,printPage,totalEaCount", box, orders, printPage, totalEaCount));
		return template.getTemplate();
	} else {
		return null;
	}
}

def domainId = box.getDomainId();
queryDsl.init('com.cj.mps3.entity.JobOrder');

def sql = """
	select 
		a.vehicle_no, a.alloc_no, a.pass_stop_nm, a.pack_type, a.order_id, a.sku_barcd, a.sku_nm, picked_qty 
	from 
		(select 
			vehicle_no, alloc_no, pass_stop_nm, pack_type, order_id, sku_cd as sku_barcd, sku_nm
		from 
			tb_if_order 
		where
			domain_id = ${domainId}
			and batch_id = '${box.getBatchId()}'
		group by
			vehicle_no, alloc_no, pass_stop_nm, pack_type, order_id, sku_cd, sku_nm
		) a

		inner join

		(
		select
			order_id,
			sku_cd as sku_barcd,
			sum(picked_qty) as picked_qty
		from
			tb_if_box_detail
		where
			domain_id = ${domainId}
			and box_result_id = '${box.getId()}'
		group by
			order_id, sku_cd
		) b
		on a.order_id = b.order_id and a.sku_barcd = b.sku_barcd

	order by a.sku_nm asc
"""

def orders = queryDsl.selectSql(sql, 0, 0);
def orderSlices = [];
def printPage = 0;
int totalEaCount = 0;

orders.eachWithIndex { o, i ->
	totalEaCount += o['picked_qty'];
}

orders.eachWithIndex { o, i ->
	orderSlices.add(o);

	if(orderSlices.size() >= 10) {
		printPage = printPage + 1;
		printBarcodeLabel(domainId, printer, printCount, printPage, box, orderSlices, totalEaCount);
		orderSlices.clear();
	}
}

if(orderSlices.size() > 0) {
	printPage = printPage + 1;
	printBarcodeLabel(domainId, printer, printCount, printPage, box, orderSlices, totalEaCount);
	orderSlices.clear();
}

return true;
